sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	//gs.info('Executing rule');
	//gs.info('current arbitrator is ' + current.u_arbitrator);
	var count = {}; // setup empty count object
	var grUserRoles = new GlideRecord('sys_user_has_role');
	grUserRoles.addQuery('role', 'afc7904c0f321300475d8e8ce1050ec8');// arbitrate.arbitrator role
	// 3Dafc7904c0f321300475d8e8ce1050ec8
	// 282bf1fac6112285017366cb5f867469
	grUserRoles.query();
	//gs.info('Query and string success');
	while (grUserRoles.next()) {
		// get user reference
		var user = grUserRoles.user + '';
		//num++;
		//gs.info(grUserRoles.user + '');
		// if not in the count add it to the count with 0 
		if (!count[user]) {
			count[user] = 0;
		}
	}
// 		gs.info('the number of keys in count is' + Object.keys(count).length);

	//create cases record query for 'in progress' cases
	var caseRecords = new GlideRecord('x_230726_arbitrate_cases');
	//caseRecords.addQuery('u_status','>=', 0); // where case is currently in progress
	caseRecords.query();
	// init least variables at high amount
	// iterate an increment if arbitrator already on a case
	//gs.info('number of queries found = ' + caseRecords.getRowCount());
	while (caseRecords.next()) {
		var caseArbitrator = caseRecords.u_arbitrator + '';
		if (count[caseArbitrator] >= 0) {
			count[caseArbitrator]++;
		}
	}
	//gs.info('madeit')
	var leastCount = 1000000;
	var leastUser = '';
	// iterate through keys in count
	for (var userRef in count) {
		// if count is less than current least
		if (count[userRef] < leastCount) {
			// reassign least count and user
			//gs.info('current user is ' + userRef;
			leastUser = userRef;
			leastCount = count[userRef];
		}
	}
	//gs.info('least user is ' + leastUser);
	g_scratchpad.assignedArbitrator = leastUser;

	//gs.info('Scratchpad is '+ g_scratchpad.assignedArbitrator);
})(current, previous);
