sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	// set the offender field as editiable
	g_form.setReadOnly('u_offending_party', false);
	// create new GlideAjax Object
	var getAssignedToFromNeedIt = new GlideAjax("getAssignedToFromNeedIt");
	// add params to send with request
	getAssignedToFromNeedIt.addParam('sysparm_name', 'getAssignedID');
	getAssignedToFromNeedIt.addParam('sysparm_needItID', g_form.getValue("u_needit_number"));
	// alert(g_form.getValue("u_needit_number")); // alert the value of the reference for purpose of 
	// send request and execute callback on results
	getAssignedToFromNeedIt.getXML(populateOffendingParty);
}
// calback for response
function populateOffendingParty(response) {
	var userFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
	g_form.clearValue('u_offending_party');
	g_form.setValue('u_offending_party', userFromScriptInclude);
	g_form.setReadOnly('u_offending_party', true);
}
