sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	if (newValue == "task") {
		var getAssignedToFromTask = new GlideAjax("getAssignedToFromNeedIt");
		getAssignedToFromTask.addParam('sysparm_taskID');
		getAssignedToFromTask.getXML(populateOffendingParty);
	}
   //Type appropriate comment here, and begin script below

}

function populateOffendingParty(response) {
	var userFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_offending_party');
		g_form.setValue('u_offending_party', userFromScriptIclude);
}
