sys_script_include.script:
var getAssignedToFromNeedIt = Class.create();
getAssignedToFromNeedIt.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getAssignedID: function() {
		// get the reference to User table from Assigned To field in Task
		//
		gs.info("What the hell?");
		//
		var needItRecord = new GlideRecord("x_58872_needit_needit");
		needItRecord.get(this.getParameter("sysparm_needItID"));
		var assignedTo = needItRecord.u_requested_for; // change to assigned to***
		gs.info(assignedTo + "is the pserson assigned");
		// use reference to get the name of the User
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(assignedTo);
		var userName = userRecord.name;
		// return the name for use in client

		return userName + "";

	},
    type: 'getAssignedToFromNeedIt'
});
