sys_script_include.script:
var getUserFromNeedIt = Class.create();
getUserFromNeedIt.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getID: function() {
		// get the reference to User table from Assigned To field in Task
		var needItRecord = new GlideRecord("needit");
		needItRecord.get(this.getParameter("sysparm_needItID"));
		var assignedTo = needItRecord.requested_for; // change to assigned to***
		// use reference to get the name of the User
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(assignedTo);
		var userName = userRecord.name;
		// return the name for use in client
		return userName;

	},
    type: 'getUserFromNeedIt'
});
