sys_ui_action.script:
function confirmApproval() {
	var confirmation = confirm('Do you want to request an early Decision?');

	if (confirmation) {
		var user = g_user.userID;
		var caseNum = g_form.getUniqueValue(); 
		var earlyDecisionAttempt = new GlideAjax('attemptEarlyDecision');
		alert('Ready for script inlcude! + ' + user + ' WUT ' +  caseNum);
		earlyDecisionAttempt.addParam('sysparm_name', 'processEarlyDecision');
		earlyDecisionAttempt.addParam('sysparm_userId', user);
		earlyDecisionAttempt.addParam('sysparm_caseNum', caseNum);
		earlyDecisionAttempt.getXML(callback);
		//alert('Ready for script inlcude! + ' + g.userID + ' WUT ' +  g_form.getUniqueValue());



	}
}

function callback(response) {
	var respFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");


	if (respFromScriptInclude === 'success') {
		alert('you have successfuly approved early decision');
	} else {
		alert('You were unable to process the decsion. Please Complete process at self service portal');
	}

}
