sysauto_script.script:
// get all case that are currently filed
var now = new GlideDateTime();
gs.info('now is ' + now.getDate() + now.getTime());

var filedCases = new GlideRecord('x_230726_arbitrate_cases');
filedCases.addQuery('u_status', 'filed');
filedCases.query();
// for all filed records
while(filedCases.next()) {
	var deadline = new GlideDateTime(filedCases.sys_created_on);
	gs.info(filedCases.number + ' Before Adjustment ' + deadline.getDate() + deadline.getTime());
	deadline.addDaysLocalTime(3);
	gs.info(filedCases.number + ' After Adjustment to Dealine ' + deadline.getDate() + deadline.getTime());
	// if after deadline auto-update status
	if (now.after(deadline)) {
		filedCases.u_status = 'in_progress';
		filedCases.update();
		gs.info('update a case!');
	}
}

gs.info('Ev');
